mapper.select:
SELECT
    p.PROJECT_ID,
    p.PROJECT_NAME,
    p.PROJECT_TYPE,
    p.PROJECT_CLASS,
    p.PROJECT_MANAGER,
    p.SALES,
    p.PROJECT_START_DATE,
    p.PROJECT_END_DATE,
    division.ORGANIZATION_NAME AS DIVISION_NAME,
    dept.ORGANIZATION_NAME AS ORGANIZATION_NAME
FROM
    PROJECT p
    LEFT JOIN ORGANIZATION dept ON p.ORGANIZATION_ID = dept.ORGANIZATION_ID
    LEFT JOIN ORGANIZATION division ON dept.UPPER_ORGANIZATION = division.ORGANIZATION_ID


        AND division.ORGANIZATION_ID = #{divisionId}


        AND dept.ORGANIZATION_ID = #{organizationId}


        AND p.PROJECT_TYPE IN

            #{type}



        AND p.PROJECT_CLASS IN

            #{classType}



        AND p.SALES >= #{salesFrom}


        AND p.SALES <= #{salesTo}


        AND p.PROJECT_START_DATE >= #{projectStartDateFrom}


        AND p.PROJECT_START_DATE <= #{projectStartDateTo}


        AND p.PROJECT_END_DATE >= #{projectEndDateFrom}


        AND p.PROJECT_END_DATE <= #{projectEndDateTo}


        AND p.PROJECT_NAME LIKE CONCAT('%', #{projectName}, '%')


ORDER BY p.PROJECT_ID
